import React, { useState } from "react";
import "../Styles/MusicPlayer.css";
import { FaHeart, FaRegHeart, FaStepBackward, FaStepForward } from "react-icons/fa";
import { BsDownload } from "react-icons/bs";

function MusicPlayer(song, imgSrc) {
  const [isLove, setLoved] = useState(false);

  const changeLoved = () => {
      setLoved(!isLove)
  }

  console.log(song, imgSrc);
  return (
    <div className="musicPlayer">
      <div className="songImage"></div>
      <div className="songAttributes">
        <audio src={song} preload="metadata" />

        <div className="top">
          <div className="left">
            <div className="loved" onClick={changeLoved}>
            {isLove ? (
              <i>
                <FaHeart />
              </i>
            ) : (
                <i>
                  <FaRegHeart />
              </i>
            )}
            </div>

                <div className="download"><i><BsDownload /></i></div>

          </div>
          <div className="middle">
              <div className="back">
                  <i><FaStepBackward/></i>
                  <i><FaStepForwards/></i>
              </div>
              <div className="palyPause"> </div>
              <div className="forward"></div>
          </div>
          <div className="right"></div>
        </div>
        <div className="bottom"></div>
      </div>
    </div>
  );
}

export { MusicPlayer };
