import React, { useState, useRef, useEffect } from "react";
import "../Styles/MusicPlayer.css";
import {
  FaBackward,
  FaForward,
  FaHeart,
  FaPause,
  FaPlay,
  FaRegHeart,
  FaShareAlt,
  FaStepBackward,
  FaStepForward,
} from "react-icons/fa";
import { BsDownload } from "react-icons/bs";


function MusicPlayer( {song, imgSrc} ) {
  const [isLove, setLoved] = useState(false);
  const [isPlaying, setPlaying] = useState(false);
  const [duration, setDuration] = useState (0);

  const audioPlayer = useRef();  //audio tag
  const progressBar = useRef();  //progressbar

  useEffect(() =>{
    const seconds = Math.floor(audioPlayer?.current?.duration);
    setDuration(seconds);
  },
   [audioPlayer?.current?.loadedmetadata,audioPlayer?.current?.readyState,]);

   const CalculateTime = (sec) => {
     const minutes = Math.floor(sec / 60);
     const returnMin = minutes < 10 ? `0${minutes}` : `${minutes}`;

     const seconds = Math.floor(sec % 60);
     const returnSec = minutes < 10 ? `0${seconds}` : `${seconds}`;

     return `${returnMin}:${returnSec}`;
   };

  const changePlayPause = () => {
   
    const prevValue = isPlaying;
    if(!prevValue){
      audioPlayer.current.play();
    }else {
      audioPlayer.current.pause();
    }
    setPlaying(!prevValue);
  };

  const changeLoved = () => {
    setLoved(!isLove);
    
  };

  // console.log(song, imgSrc);
  return (
    <div className="musicPlayer">
      <div className="songImage">
        <img src= {imgSrc} alt="check connection" />
      </div>
      <div className="songAttributes">
        <audio src={song} preload="metadata" ref={audioPlayer}/>

        <div className="top">
          <div className="left">
            <div className="loved" onClick={changeLoved}>
              {isLove ? (
                <i>
                  <FaHeart />
                </i>
              ) : (
                <i>
                  <FaRegHeart />
                </i>
              )}
            </div>

            <div className="download">
              <i>
                <BsDownload />
              </i>
            </div>
          </div>
          <div className="middle">
            <div className="back">
              <i>
                <FaStepBackward />
              </i>
              <i>
                <FaBackward />
              </i>
            </div>
            <div className="palyPause" onClick={changePlayPause}>
              {isPlaying ? (
                <i>
                  {" "}
                  <FaPause />
                </i>
              ) : (
                <i>
                  <FaPlay />
                </i>
              )}
            </div>
            <div className="forward">
              <i>
                <FaForward />
              </i>
              <i>
                <FaStepForward />
              </i>
            </div>
          </div>
          <div className="right">
            <i>
              <FaShareAlt />
            </i>
          </div>
        </div>
        <div className="bottom">
          <div className="currentTime">00:00</div>
          <input type="range" className="progressBar" ref={progressBar}/>
          <div className="duration"> 
          {(duration && !isNaN(duration) && CalculateTime(duration)) ? 
                CalculateTime(duration)
              :"00:00"}
          </div>
        </div>
      </div>
    </div>
  );
}

export { MusicPlayer };
